use yew::prelude::*;
use stylist::yew::{styled_componnent};
use stylist::style; #[derive(Properties, PartialEq)]
pub struct Props { pub prop_one: String, pub prop_two: Option<String>, //* Here we use an enum "Level" to create a super property pub prop_three: Level,
} //* You must define the enum used to create a super property using 'derive(PartialEq)', like so;
#[allow(unused)]
#[derive(PartialEq)]
pub enum Level { Standard, Pro, Premium, Gold,
} //* To work with an enum-powered super property, you need to be able to make transformations on it based on its variants
//* ..to do this, you need to impl the enum, e.g;
impl Level { pub fn assign_level(&self) -> String { match self { Level::Standard => "standard".to_owned(), Level::Pro => "pro".to_owned(), Level::Premium => "premium".to_owned(), Level::Gold => "gold".to_owned(), } }
} #[styled_component(SubComponentsAComponent1)]
pub fn sub_components_a_component_1(prop: &Props) -> Html { //* Enum super property "Level" will be used to assign badge let badge = style!( r#" .standard { color: orange; } .pro { color: green; } .premium { color: blue; } .gold { color: gold; } "# ).unwrap(); // end of 'style!' //* Make use of "badge" (style) to be able to assign level as class to nested elements/components html! { <div class={badge}> <p>{"Hi! I am from component file 'src/components/sub_components_a/sub_components_a_component_1.rs'"}</p> <p>{"...and I was passed property/properties: "} {&prop.prop_one.to_owned()}</p> <p>{"- '"} {&prop.prop_one.to_owned()} {"'"}</p> <p>{"AND enum; "}</p> { //* Time to assign a level class. Please remember to delete this comment block-scope } <span class={&prop.prop_three.assign_level()}>{"- "}{&prop.prop_three.assign_level()} {" level"}</span> </div> }
} INSIDE 'yew_project_name/src/lib.rs' use yew::prelude::*; mod components; //* When using a sub-component with an an enum powered (super) property, you must also use the 'enum' itself
//* ..for example, "SubComponentsAComponent1" utilizes enum "Level" (as a super property)
//* ..therefore, it ("Level") must be used too, like so;
use components::sub_components_a::sub_components_a_component_1::{SubComponentsAComponent1, Level}; // Note the presence of "Level" #[function_component(App)]
pub fn app() -> Html { html! { <> <div> { //* Time to pass enum-powered super property'prop_three' of type enum "Level" to SubComponentsAComponent1 into embeded component SubComponentsAComponent1 which is embedded herein } <SubComponentsAComponent1 prop_one="Confirming prop one value" prop_three={Level::Pro} /> {/*Notice that "prop_two" is not passed any value because doing so is absolutely optional*/} </div> </> } } INSIDE 'yew-project-name/src/main.rs' use yew_project_name::App; fn main() { yew::start_app::<App>();
}

