#[derive(Properties, PartialEq)]
pub struct Props { pub prop_one: String, pub prop_two: Option<String>, // * Here we are defining the to-be-sent data for our callback pub to_be_sent_data: Callback<String>,
} #[function_component(SubComponentsAComponent1)]
pub fn sub_components_a_component_1(prop: &Props) -> Html { // * Time to send 'to_be_sent_data' up from this component // * ..into whatever component it is being embedded in // * ..as a child component prop.to_be_sent_data.emit( "' Sent as callback data from child component 'SubComponentsAComponent1' to parent component 'App' '".to_owned(), ); html! { <div> <p>{"Hi! I am from component file 'src/components/sub_components_a/sub_components_a_component_1.rs'"}</p> <p>{"...and I was passed property/properties: "} {&prop.prop_one.to_owned()}</p> <p>{"Please open your Dev Tools to see the logged callback data."}</p> </div> } } INSIDE 'yew_project_name/src/lib.rs' use yew::prelude::*;
use gloo::console::log;
use components::sub_components_a::sub_components_a_component_1::SubComponentsAComponent1; mod components; #[function_component(App)]
pub fn app() -> Html { // * Create a callback (action) using 'Callback::from' available in yew's prelude let callback_receipient_action = Callback::from ( |received_from_child_component: String| { //* Perform some action with data received from child component // ..I have decided to simply log it log!("Here is what was ", received_from_child_component) } ); // * You also need to assign the callback action of the parent element // * ..to the callback property of the child component emitting // * ..data to the parent component. // * Basically, the parent components needs to acknowledge the child component's callback like so; html! { <> <div> <SubComponentsAComponent1 prop_one="hi" to_be_sent_data={callback_receipient_action} /> </div> </> }
} INSIDE 'yew-project-name/src/main.rs' use yew_project_name::App; fn main() { yew::start_app::<App>();
}

