pub fn app() -> Html { let external_style = Style::new(EXTERNAL_STYLE).unwrap(); // * Create a receipient for your child component's "states_data" callback property // * ..and do anything with it; send it, log it, anything. This could be where you write code logic to submit form data. let on_submit_form_callback_property_receipient = Callback::from( //* Remember to type-annotate "received_states_data" so the compiler can know what data type it is. |received_states_data: StatesData| { log!( "State 1 (an input) form-data is: ", received_states_data.input_state.clone() ); log!( "State 2 (click-count) form-data is: ", received_states_data.clicks_count_state ); log!( "State 3 (message input) form-data is: ", received_states_data.extra_message_state.clone() ); }, ); html! { <> <div class={external_style}> <p> {"A Form Demo!"} </p> <b> {"Please note that the html 'form' element is being used this demo, and form submission action is being simulated. Please open your Developer console to see the form data that is being simulated as submitted."} </b> <SubComponentsAComponent1 prop_one="hi" /> <FormAssemblerComponent input_one_value="Input 1" input_two_value="Input 2" button_name="Button 1 (now a submit button too)" on_submit_form_callback={on_submit_form_callback_property_receipient} /> </div> </> }
